package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityAntmanBinding

class AntmanActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityAntmanBinding.inflate(layoutInflater)
        setContentView(binding.root)


        Glide.with(this@AntmanActivity)
            .load("data:image/jpeg;base64,")
            .into(binding.scott)
        Glide.with(this@AntmanActivity)
            .load("data:image/jpeg;base64,")
            .into(binding.antman)
        Glide.with(this@AntmanActivity)
            .asGif()
            .load("https://giffiles.alphacoders.com/210/210118.gif")
            .into(binding.suitup)

        binding.scott.setOnClickListener{
            onBackPressed()
        }
    }
}