package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityBlackWidowBinding


class BlackWidowActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityBlackWidowBinding.inflate(layoutInflater)
        setContentView(binding.root)
        Glide.with(this@BlackWidowActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.nat)

        Glide.with(this@BlackWidowActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.widow)
        Glide.with(this@BlackWidowActivity)
                .asGif()
                .load("https://t1.daumcdn.net/cafeattach/1D7bO/1d708917a735e54337b819ef0291a1c26d3cc057")
                .into(binding.suitup)
        binding.nat.setOnClickListener{
            onBackPressed()
        }
    }
}