package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityHulkBinding

class HulkActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityHulkBinding.inflate(layoutInflater)
        setContentView(binding.root)
        Glide.with(this@HulkActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.banner)

        Glide.with(this@HulkActivity)
                .load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTDR843PzEXpRX8ECEkKrHOKjAKIkMKyu_8-g&usqp=CAU")
                .into(binding.hulk)

        Glide.with(this@HulkActivity)
                .asGif()
                .load("https://upload2.inven.co.kr/upload/2018/10/29/bbs/i13399625744.gif")
                .into(binding.suitup)
        binding.banner.setOnClickListener{
            onBackPressed()
        }

    }
}