package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityIronmanBinding

class IronmanActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityIronmanBinding.inflate(layoutInflater)
        setContentView(binding.root)

        Glide.with(this@IronmanActivity)
            .load("data:image/jpeg;base64,")
            .override(600 ,400)
            .into(binding.ironMan)

        Glide.with(this@IronmanActivity)
            .load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSn_3r1IXNgz5kpqKvBOKZYJlVxpuLL6ECOcw&usqp=CAU")

            .into(binding.stark)

        Glide.with(this@IronmanActivity)
            .asGif()
            .load("https://thumbs.gfycat.com/EnchantingGiddyFlickertailsquirrel-max-1mb.gif")
            .into(binding.suitup)
        binding.ironMan.setOnClickListener{
            onBackPressed()
        }
    }
}