package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityThorBinding

class ThorActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityThorBinding.inflate(layoutInflater)
        setContentView(binding.root)
        Glide.with(this@ThorActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.thor)

        Glide.with(this@ThorActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.thunder)

        Glide.with(this@ThorActivity)
                .asGif()
                .load("https://media.tenor.com/images/bc8ff9b0f2271982a4592c205c9084b6/tenor.gif")
                .into(binding.suitup)
        binding.thor.setOnClickListener{
            onBackPressed()
        }
    }
}