package com.example.movieintroduction

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.movieintroduction.databinding.ActivityWandaBinding

class WandaActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityWandaBinding.inflate(layoutInflater)
        setContentView(binding.root)
        Glide.with(this@WandaActivity)
                .load("data:image/jpeg;base64,")
                .into(binding.wanda)

        Glide.with(this@WandaActivity)
                .load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRyoG4ctDtAamvncjOOqC0aC804i0Y2kW56kQ&usqp=CAU")
                .into(binding.scarlet)

        Glide.with(this@WandaActivity)
                .asGif()
                .load("https://data.whicdn.com/images/333601350/original.gif")
                .into(binding.suitup)
        binding.wanda.setOnClickListener{
            onBackPressed()
        }
    }
}